import random

class EnemyReport:
    def __init__(self):
        self.enemies = {}

    def add_enemy(self, name, details):
        if name in self.enemies:
            print(f"Enemy {name} already reported.")
        else:
            self.enemies[name] = {
                'details': details,
                'reporter': f'User_{random.randint(1, 1000)}'
            }
            print(f"{name} has been successfully reported.")

    def get_enemy_reports(self):
        for enemy in self.enemies.values():
            print(f"Enemy: {enemy['reporter']}, Details: {enemy['details']}")

