{
  Serial.begin(9600);
  gpsSerial.begin(4800);
}

void loop()
{
  while (gpsSerial.available() > 0)
    if (gpsSerial.read() == 0x1B)
      break;

  if (gpsSerial.available() > 0)
    {
      TinyGPS gps;
      char buff[100];
      int len = gps.get_position(&buff, sizeof(buff));
      // Send GPS data to server using HTTP or HTTPS request
      sendGPSData(buff);
    }
}

void sendGPSData(char* gpsData)
{
  String url = "https://maps.googleapis.com/maps/api/staticmap/image?center=" + String(gpsData) + "&zoom=13&size=400x300&markers=color:red%7Clabel:S%7C" + String(gpsData) + "&key=[Your API Key]";
  WiFiClient client;
  client.setServer("maps.googleapis.com", 443);
  client.print("GET " + url + " HTTP/1.0\r\n");
  client.print("Host: maps.googleapis.com\r\n");
  client.print("\r\n");
  int responseCode = client.responseCode();
